import { useEffect, useState } from "react";
import NavBarTop from "../../components/NavBarTop";
import css from "./styled.module.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PerfilCliente = () => {
    const [ address, setAddress ] = useState(null);
    const [ user, setUser ] = useState();
    const optionRouter = ["Geral", "Endereços", "Pedidos"];

    const navigate = useNavigate();

    const id = localStorage.getItem("id");

    useEffect(() => {
        axios.get(`https://localhost:7221/api/ClientProfile/get-by-idclient/${id}`)
        .then((res) => {
            setUser(res.data);
        })
        .catch((error) => console.log(error))
    }, []);

    const redirectRouterPerfil = (option) => {
        switch (option){
            case "Geral":
                navigate("/perfil")
                break;
            case "Geral":
                navigate("/perfil")
                break;
            case "Geral":
                navigate("/perfil")
                break;
        }
    };

    return(
        <>
            <NavBarTop />
            <div className={css.container}>
                <div className={css.tittle}>
                    <h2>Meu Perfil</h2>
                    <button>EDITAR</button>
                </div>
                <div className={css.switch_route_perfil}>
                   {optionRouter.map()}
                    <div className={css.card_switch} onClick={() => redirectRouterPerfil()}>
                        <p>Endereços</p>
                    </div>
                    <div className={css.card_switch} onClick={() => redirectRouterPerfil()}>
                        <p>Pedidos</p>
                    </div>
                </div>
                <div className={css.container_form_user}>
                    <div>
                        <span>Nome:</span>
                        <span>{user?.name || "Nome não cadastrado"}</span>
                    </div>
                    <hr />
                    <div>
                        <span>CPF:</span>
                        <span>{user?.document || "Documento não cadastrado"}</span>
                    </div>
                    <hr />
                    <div>
                        <span>Telefone:</span>
                        <span>{user?.telPhone || "Telefone não cadastrado"}</span>
                    </div>
                    <hr />
                    <div>
                        <span>Celular:</span>
                        <span>{user?.cellPhone || "Celular não cadastrado"}</span>
                    </div>
                    <hr />
                    <div>
                        <span>Título:</span>
                        <span>{user?.tittleStatus || "0"}</span>
                    </div>
                    <hr />
                    <div>
                        <span>Pedidos:</span>
                        <span>{user?.orders || "0"}</span>
                    </div>
                </div>
            </div>
        </>
    )
}

export default PerfilCliente;
